// go get -v github.com/veandco/go-sdl2/sdl

type Player struct {
	PosX   float64
	PosY   float64
	DirX   float64
	DirY   float64
	PlaneX float64
	PlaneY float64
	Angle  float64
	LookY  int32
	Speed  float64
}

type Keyboard struct {
	KeyUp       int32
	KeyDown     int32
	KeyLeft     int32
	KeyRight    int32
	KeyAction   int32
	KeyLookUp   int32
	KeyLookDown int32
}

